// routes/products.js
const express = require('express');
const router = express.Router();
const Product = require('../models/Product');

// GET /api/products  -> list all products
router.get('/', async (req, res) => {
  try {
    const products = await Product.find().sort({ createdAt: -1 });
    res.json(products);
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

// POST /api/products -> create new product
router.post('/', async (req, res) => {
  try {
    const { name, sku, quantity, price } = req.body;
    if (!name || !sku) return res.status(400).json({ error: 'name and sku required' });

    const newProduct = new Product({ name, sku, quantity, price });
    const saved = await newProduct.save();
    res.status(201).json(saved);
  } catch (err) {
    // duplicate sku handling
    if (err.code === 11000) return res.status(400).json({ error: 'SKU already exists' });
    res.status(500).json({ error: err.message });
  }
});

// DELETE /api/products/:id -> delete by id
router.delete('/:id', async (req, res) => {
  try {
    const removed = await Product.findByIdAndDelete(req.params.id);
    if (!removed) return res.status(404).json({ error: 'Product not found' });
    res.json({ message: 'Deleted', product: removed });
  } catch (err) {
    res.status(500).json({ error: err.message });
  }
});

module.exports = router;
